function initSidebar(){const hamburger=document.getElementById('hamburger');const sidebar=document.getElementById('sidebar');if(!hamburger||!sidebar){console.warn('Sidebar:No se encontrÃ³ hamburger o sidebar en el DOM');return;}hamburger.addEventListener('click',()=>{sidebar.classList.toggle('hidden');});const links=sidebar.querySelectorAll('a');links.forEach(link=>{link.addEventListener('click',()=>{if(window.innerWidth<=768){sidebar.classList.add('hidden');}});});document.addEventListener('click',(e)=>{if(!sidebar.contains(e.target)&&!hamburger.contains(e.target)){if(window.innerWidth<=768){sidebar.classList.add('hidden');}}});window.addEventListener('resize',()=>{if(window.innerWidth>768){sidebar.classList.remove('hidden');}else{sidebar.classList.add('hidden');}});if(window.innerWidth<=768){sidebar.classList.add('hidden');}}document.addEventListener('DOMContentLoaded',initSidebar);